import React, { createContext, useState } from "react";
import PropTypes from "prop-types"; // PropTypes'ı içeri aktarın

// GlobalContext'yi tanımlıyoruz
export const GlobalContext = createContext();

// GlobalProvider component'ini oluşturuyoruz
export const GlobalProvider = ({ children }) => {
  const [cart, setCart] = useState([]);  // Sepet durumunu oluşturuyoruz
  const [orders, setOrders] = useState([]);  // Sipariş durumunu oluşturuyoruz

  const addItemToOrderList = (order) => {
    setOrders((prevOrders) => [...prevOrders, order]);  // Sipariş ekliyoruz
  };

  const clearCart = () => {
    setCart([]);  // Sepeti temizliyoruz
  };

  return (
    <GlobalContext.Provider value={{ cart, orders, addItemToOrderList, clearCart }}>
      {children}
    </GlobalContext.Provider>
  );
};

GlobalProvider.propTypes = {
  children: PropTypes.node.isRequired,  // children prop'unu doğruluyoruz
};
