import React, { createContext, useState } from "react";
import PropTypes from "prop-types"; // prop-types'ı içe aktarın

// GlobalState context'iniz ve diğer fonksiyonlar burada

const GlobalState = ({ children }) => {
  // context setup ve state yönetimi burada

  return (
    <GlobalContext.Provider value={{ /* provider değerleri */ }}>
      {children}
    </GlobalContext.Provider>
  );
};

// children prop'unu doğrulamak
GlobalState.propTypes = {
  children: PropTypes.node.isRequired, // children prop'unun bir node olmasını bekliyoruz
};

export default GlobalState;
