// src/redux/store.js
import { configureStore } from "@reduxjs/toolkit";
import itemsReducer from "./slices/itemsSlice";  // items slice'ını import et
import cartReducer from "./slices/cartSlice";    // cart slice'ını import et

const store = configureStore({
  reducer: {
    items: itemsReducer,
    cart: cartReducer,
  },
});

export default store;
