import React, { createContext, useState } from "react";
import PropTypes from "prop-types";  // PropTypes'ı import edin

// GlobalContext oluşturuluyor
export const GlobalContext = createContext();

// GlobalProvider bileşeni, state'leri sağlamak için kullanılır
export const GlobalProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addItemToCartList = (item) => {
    setCart((prevCart) => [...prevCart, item]);
  };

  return (
    <GlobalContext.Provider value={{ cart, addItemToCartList }}>
      {children}
    </GlobalContext.Provider>
  );
};

// children props'unu doğrulamak için propTypes ekleyin
GlobalProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
