// src/redux/slices/itemsSlice.js
import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [], // Ürünlerin olduğu liste
  filteredItems: [], // Filtrelenmiş ürünler
};

const itemsSlice = createSlice({
  name: "items",
  initialState,
  reducers: {
    setItems: (state, action) => {
      state.items = action.payload; // Tüm ürünleri al
      state.filteredItems = action.payload; // Başlangıçta filtrelenmiş ürünler de aynı
    },
    filterByCategory: (state, action) => {
      const category = action.payload;
      state.filteredItems = state.items.filter((item) => item.category === category); // Kategorilere göre filtrele
    },
  },
});

export const { setItems, filterByCategory } = itemsSlice.actions;
export default itemsSlice.reducer;
