import React, { createContext, useState } from "react";

// GlobalContext oluşturuluyor
export const GlobalContext = createContext();

// GlobalProvider, context'i sağlayan provider fonksiyonu
export const GlobalProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);

  // Sepete yeni ürün ekleme fonksiyonu
  const addItemToCartList = (item) => {
    // Eğer ürün zaten sepetteyse, eklememek için kontrol ediyoruz
    const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
    
    if (!isItemInCart) {
      setCart([...cart, item]); // Ürün sepete ekleniyor
    }
  };

  return (
    <GlobalContext.Provider value={{ cart, orders, addItemToCartList }}>
      {children}
    </GlobalContext.Provider>
  );
};
