import React, { useReducer } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

// Reducer fonksiyonu
const filterReducer = (state, action) => {
  switch (action.type) {
    case "FILTER_BY_CATEGORY":
      return action.payload;
    default:
      return state;
  }
};

const Navbar = () => {
  const [selectedCategory, dispatch] = useReducer(filterReducer, ""); // Başlangıç kategorisi boş

  const handleFilter = (category) => {
    dispatch({ type: "FILTER_BY_CATEGORY", payload: category });
  };

  return (
    <div className="navbar">
      <Link to="/">
        <h2>LikeMuClothes</h2>
      </Link>
      <ul className="navbar-ul">
        <li>
          <Link
            to="/category/women"
            onClick={() => handleFilter("Kadın")}
          >
            Kadın
          </Link>
        </li>
        <li>
          <Link
            to="/category/men"
            onClick={() => handleFilter("Erkek")}
          >
            Erkek
          </Link>
        </li>
        <li>
          <Link
            to="/category/clothing"
            onClick={() => handleFilter("Giyim")}
          >
            Giyim
          </Link>
        </li>
        <li>
          <Link
            to="/category/brands"
            onClick={() => handleFilter("Markalar")}
          >
            Markalar
          </Link>
        </li>
        <Link to="/cart">
          <li>
            &#128722;{" "}
            <span className="card-count" style={{ color: "red" }}>
              (0)
            </span>
          </li>
        </Link>
        <Link to="/orders">
          <li>Siparişler</li>
        </Link>
        <button className="nav-btn">Merhaba, Namık</button>
      </ul>
    
    </div>
  );
};

export default Navbar;
