// redux/slices/itemsSlice.js
import { createSlice } from "@reduxjs/toolkit";

// Başlangıç durumu
const initialState = {
  items: [
    { id: 1, name: "Elbise", category: "Kadın", size: "M", brand: "Zara", occasion: "Günlük", price: 40 },
    { id: 2, name: "T-shirt", category: "Erkek", size: "L", brand: "Nike", occasion: "Spor", price: 30 },
    { id: 3, name: "Ceket", category: "Kadın", size: "S", brand: "H&M", occasion: "İş", price: 60 },
    // Diğer ürünler mevcut...
  ],
  filteredItems: [], // Filtrelenmiş ürünler
  selectedCategory: "", // Seçilen kategori
};

// itemsSlice oluşturuluyor
const itemsSlice = createSlice({
  name: "items",
  initialState,
  reducers: {
    // Kategoriye göre ürünleri filtreleyen action
    filterByCategory: (state, action) => {
      const category = action.payload;
      state.selectedCategory = category; // Seçilen kategoriyi kaydediyoruz
      state.filteredItems = state.items.filter(item => item.category === category); // Filtreleme işlemi
    },
  },
});

// actions ve reducer'ı dışa aktarıyoruz
export const { filterByCategory } = itemsSlice.actions;
export default itemsSlice.reducer;
