import React, { useContext } from "react";
import { GlobalContext } from "./GlobalContext";

const Checkout = () => {
  const { orders } = useContext(GlobalContext); // orders'ı buradan alıyoruz

  const handlePay = () => {
    // orders'ı kontrol et
    if (orders && orders.length > 0) {
      // orders varsa ödeme işlemi başlat
      console.log("Ödeme işlemi başlatılıyor.");
    } else {
      // orders yoksa hata mesajı
      console.log("Siparişler listesi boş veya undefined");
    }
  };

  return (
    <div>
      <button onClick={handlePay}>Ödeme Yap</button>
    </div>
  );
};

export default Checkout;
